// Load header and footer
document.addEventListener('DOMContentLoaded', () => {
    const loadComponents = async () => {
      try {
        // Load header
        const headerResponse = await fetch('header.html');
        if (!headerResponse.ok) throw new Error('Failed to load header');
        document.getElementById('header').innerHTML = await headerResponse.text();
        
        // Load footer
        const footerResponse = await fetch('footer.html');
        if (!footerResponse.ok) throw new Error('Failed to load footer');
        document.getElementById('footer').innerHTML = await footerResponse.text();
        
        // Initialize mobile menu
        initMobileMenu();
        
        // Initialize dropdown menus
        initDropdowns();
        
        // Load posts
        loadPosts();
        
        // Initialize redirect timer
        initRedirectTimer();
      } catch (error) {
        console.error('Error loading components:', error);
        document.getElementById('header').innerHTML = '<p class="error">Error loading header</p>';
        document.getElementById('footer').innerHTML = '<p class="error">Error loading footer</p>';
      }
    };
    
    loadComponents();
  });
  
  // Mobile menu functionality
  function initMobileMenu() {
    const hamburger = document.querySelector('.hamburger');
    const navMenu = document.querySelector('.nav-menu');
    const closeMenu = document.querySelector('.close-menu');
    const overlay = document.querySelector('.overlay');
    
    if (hamburger && navMenu && closeMenu) {
      hamburger.addEventListener('click', () => {
        navMenu.classList.add('active');
        overlay.style.display = 'block';
        document.body.style.overflow = 'hidden';
      });
      
      closeMenu.addEventListener('click', () => {
        navMenu.classList.remove('active');
        overlay.style.display = 'none';
        document.body.style.overflow = '';
      });
      
      overlay.addEventListener('click', () => {
        navMenu.classList.remove('active');
        overlay.style.display = 'none';
        document.body.style.overflow = '';
      });
    }
  }
  
  // Dropdown menus functionality
  function initDropdowns() {
    const dropdownToggles = document.querySelectorAll('.dropdown-toggle');
    
    dropdownToggles.forEach(toggle => {
      toggle.addEventListener('click', (e) => {
        e.preventDefault();
        const parent = toggle.closest('.dropdown');
        const menu = parent.querySelector('.dropdown-menu');
        
        // Close all other dropdowns
        document.querySelectorAll('.dropdown-menu').forEach(m => {
          if (m !== menu) m.classList.remove('show');
        });
        
        menu.classList.toggle('show');
      });
    });
    
    // Close dropdowns when clicking outside
    document.addEventListener('click', (e) => {
      if (!e.target.closest('.dropdown')) {
        document.querySelectorAll('.dropdown-menu').forEach(menu => {
          menu.classList.remove('show');
        });
      }
    });
  }
  
  // Load and display posts
  async function loadPosts() {
    try {
      const response = await fetch('posts.json');
      if (!response.ok) throw new Error('Failed to load posts');
      const posts = await response.json();
      
      const postList = document.getElementById('post-list');
      if (!postList) return;
      
      if (posts.length === 0) {
        postList.innerHTML = '<p class="no-posts">No featured posts available</p>';
        return;
      }
      
      // Clear existing content
      postList.innerHTML = '';
      
      // Create post elements
      posts.forEach(post => {
        const postElement = createPostElement(post);
        postList.appendChild(postElement);
      });
      
      // Initialize carousel if desktop
      if (window.innerWidth > 768) {
        initCarousel();
      }
    } catch (error) {
      console.error('Error loading posts:', error);
      const postList = document.getElementById('post-list');
      if (postList) {
        postList.innerHTML = '<p class="error">Error loading featured posts</p>';
      }
    }
  }
  
  function createPostElement(post) {
    const postDiv = document.createElement('div');
    postDiv.className = 'post-preview';
    
    postDiv.innerHTML = `
      <div class="post-image">
        <img src="${post.image}" alt="${post.title}" loading="lazy">
        ${post.hot ? '<span class="hot-label">HOT</span>' : ''}
      </div>
      <div class="post-content">
        <h3><a href="${post.url}">${post.title}</a></h3>
        <p>${post.excerpt}</p>
        <a href="${post.url}" class="view-more">Xem thêm</a>
      </div>
    `;
    
    return postDiv;
  }
  
  // Carousel functionality
  function initCarousel() {
    const postList = document.getElementById('post-list');
    const posts = document.querySelectorAll('.post-preview');
    const prevBtn = document.getElementById('prev-slide');
    const nextBtn = document.getElementById('next-slide');
    
    if (!postList || posts.length === 0 || !prevBtn || !nextBtn) return;
    
    let currentIndex = 0;
    const totalPosts = posts.length;
    
    // Position posts
    posts.forEach((post, index) => {
      post.style.transform = `translateX(${index * 100}%)`;
    });
    
    // Show current slide
    function showSlide(index) {
      if (index >= totalPosts) currentIndex = 0;
      if (index < 0) currentIndex = totalPosts - 1;
      
      posts.forEach((post, i) => {
        post.style.transform = `translateX(${(i - currentIndex) * 100}%)`;
      });
    }
    
    // Navigation buttons
    prevBtn.addEventListener('click', () => {
      currentIndex--;
      showSlide(currentIndex);
    });
    
    nextBtn.addEventListener('click', () => {
      currentIndex++;
      showSlide(currentIndex);
    });
    
    // Auto slide
    let slideInterval = setInterval(() => {
      currentIndex++;
      showSlide(currentIndex);
    }, 5000);
    
    // Pause on hover
    postList.addEventListener('mouseenter', () => {
      clearInterval(slideInterval);
    });
    
    postList.addEventListener('mouseleave', () => {
      slideInterval = setInterval(() => {
        currentIndex++;
        showSlide(currentIndex);
      }, 5000);
    });
  }
  
  // Redirect timer functionality
  function initRedirectTimer() {
    const timerElement = document.getElementById('redirect-timer');
    const cancelBtn = document.getElementById('cancel-redirect');
    
    if (!timerElement || !cancelBtn) return;
    
    let timeLeft = 120; // 2 minutes in seconds
    let redirectTimer = setInterval(updateTimer, 1000);
    
    function updateTimer() {
      const minutes = Math.floor(timeLeft / 60);
      const seconds = timeLeft % 60;
      
      timerElement.textContent = `Website sẽ tự động chuyển đến Fanpage IVS Academy trong ${minutes} phút ${seconds} giây...`;
      
      if (timeLeft <= 0) {
        clearInterval(redirectTimer);
        window.location.href = "https://facebook.com/hr.ivsacademy";
      }
      
      timeLeft--;
    }
    
    cancelBtn.addEventListener('click', () => {
      clearInterval(redirectTimer);
      timerElement.textContent = "Chuyển hướng đã bị hủy.";
      cancelBtn.textContent = "Đã hủy";
      cancelBtn.disabled = true;
    });
  }
  
  // Responsive adjustments
  window.addEventListener('resize', () => {
    if (window.innerWidth <= 768) {
      const postList = document.getElementById('post-list');
      if (postList) {
        postList.style.height = 'auto';
        const posts = document.querySelectorAll('.post-preview');
        posts.forEach(post => {
          post.style.transform = 'none';
          post.style.position = 'relative';
        });
      }
    }
  });